#include "teacher.h"Teacher::Teacher(){}Teacher::Teacher(int empId, string name, string pwd){    this->m_EmpId = empId;    this->m_Name = name;    this->m_Pwd = pwd;}void Teacher::operMenu(){    cout << "Welcome" << this->m_Name << "login" << endl;    cout << "\t\t ----------------------------------\n";    cout << "\t\t|                                  |\n";    cout << "\t\t|          1.View all reservation  |\n";    cout << "\t\t|                                  |\n";    cout << "\t\t|          2.Check reservation     |\n";    cout << "\t\t|                                  |\n";    cout << "\t\t|          0.Exit                  |\n";    cout << "\t\t|                                  |\n";    cout << "\t\t ----------------------------------\n";    cout << "Select your operation: " << endl;}void Teacher::showAllOrder(){    OrderFile of;    if (of.m_Size == 0)    {        cout << "No record" << endl;        system("pause");        system("cls");        return;    }    for (int i = 0; i < of.m_Size; i++)    {        cout << i + 1 << ".  ";        cout << "Reservation date: " << of.m_orderData[i]["date"];        cout << "Reservation time: " << (of.m_orderData[i]["interval"] == "1" ? "8:00 - 12:00" : "13:00 - 18:00");        cout << "Student id: " << of.m_orderData[i]["stuId"];        cout << "Student name: " << of.m_orderData[i]["stuName"];        cout << "Computer room: " << of.m_orderData[i]["roomId"];        string status = " Status ";// 1 Under review  2 Reservation made  -1 Reservation failed  0 Reservation canceled        if (of.m_orderData[i]["status"] == "1")        {            status += "Under review";        }        else if (of.m_orderData[i]["status"] == "2")        {            status += "Reservation made sucessfully";        }        else if (of.m_orderData[i]["status"] == "-1")        {            status += "Reservation failed";        }        else        {            status += "Reservation canceled";        }        cout << status << endl;    }    system("pause");    system("cls");}void Teacher::validOrder(){    OrderFile of;    if (of.m_Size == 0)    {        cout << "No record" << endl;        system("pause");        system("cls");        return;    }    vector<int>v;    int index = 0;    cout << "The reservations to be reviewed are as follows:" << endl;    for (int i = 0; i < of.m_Size; i++)    {        if (of.m_orderData[i]["status"] == "1")        {            v.push_back(i);            cout << ++index << ". ";            cout << "Reservation date: " << of.m_orderData[i]["date"];            cout << " Reservation time: " << (of.m_orderData[i]["interval"] == "1" ? "8:00 - 12:00" : "13:00 - 18:00");            cout << " Student id: " << of.m_orderData[i]["stuId"];            cout << " Student name:" << of.m_orderData[i]["stuName"];            cout << " Computer room: " << of.m_orderData[i]["roomId"];            string status = " Status: ";            cout << status << endl;        }    }    cout << "Please enter the reservations of the review. 0 represents return" << endl;    int select = 0;    int ret = 0;    while (true)    {        cin >> select;        if (select >= 0 && select <= v.size())        {            if (select == 0)            {                break;            }            else            {                cout << "Please enter the review result" << endl;                cout << "1. pass" << endl;                cout << "2. fail" << endl;                cin >> ret;                if (ret == 1)                {                                    of.m_orderData[v[select - 1]]["status"] = "2";                }                else                {                                        of.m_orderData[v[select - 1]]["status"] = "-1";                }                of.updateOrder(); // 更新预约记录                cout << "Review done!" << endl;                break;            }        }        cout << "Wrong! Please enter again " << endl;    }    system("pause");    system("cls");}